function loadCss(url,file) {
	"use strict";
    var link = document.createElement("link");
    if(!(jQuery("body").find('link').hasClass(file))) {
		link.type = "text/css";
		link.href = url;
	    link.rel = "stylesheet";
	    link.media = "all";
	    document.getElementsByTagName("body")[0].appendChild(link);
	    jQuery('body').find('link').last().addClass(file);
    }
}
